import React from 'react';

interface IProps {
  readonly shadowColor?: string;
  readonly shadowDeep?: string;
  readonly children?: any;
  readonly [otherAttrs: string]: any; // Needed to add attributes to react components whitout typescript errors
}

export default class Loader extends React.PureComponent<IProps> {

  private loaderElement: HTMLDivElement;

  private containerStyle: any = {
    zIndex: 11,
    position: 'absolute',
    width: '100%',
    top: '41%',
    textAlign: 'center',
  };

  private contentStyle: any = {
    color: 'black',
    backgroundColor: 'white',
    display: 'block',
    margin: 'auto',
    padding: '20px',
    borderRadius: '4px',
    fontFamily: 'verdana, sans-serif',
    fontSize: 'small',
    width: '70px',
    textAlign: 'center'
  };

  public constructor(props: IProps) {
    super(props);
    const shadowColor = this.props.shadowColor || 'grey';
    const shadowDeep = this.props.shadowDeep || '10';
    this.contentStyle.boxShadow = `${shadowColor} 0 0 ${shadowDeep}px 0`;
  }

  public hide() {
    this.loaderElement.classList.remove('fade-in');
    this.loaderElement.classList.add('fade-out');
    setTimeout( () => {
      this.loaderElement.style.zIndex = '-1';
    }, 1000)
  }

  public show() {
    this.loaderElement.classList.add('fade-in');
  }

  public hideWhen(entity: AFrame.Entity, eventName: string = 'loaded'): void {
    entity && entity.addEventListener(eventName, () => {
      this.hide();
    })
  }

  public render() {
    return(
      <div id="loader-element" ref={ (loader: HTMLDivElement) => this.loaderElement = loader }
        style={ this.containerStyle } className="fade-in">
        <div style={ this.contentStyle }>
          {/* IMPORTANT: loader icon must be preloaded in "index.html" header */}
          <img src="img/history3.svg" style={ {verticalAlign: 'middle'} } />
          <div style={ {paddingTop: '5px'} }>{ this.props.children }</div>
        </div>
      </div>
    )
  }
}

import React from 'react';
import {ISideMenuItem} from "./SideMenuItems";
import './SideMenuCmp.css';
const btnCloseSvg = require('./icons/close-btn.svg');


interface IProps {
  title?: any;
  items?: ISideMenuItem[];
  itemActive: string;
  children?: any;
  [otherAttrs: string]: any
}

export default class SideMenu extends React.PureComponent<IProps> {

  private sideMenuContainer: HTMLDivElement;

  public componentWillMount() {
    if (this.props.items) {
      // Initialize all sideMenuItems to inactive
      this.props.items.forEach( item => {
        item.active = false;
      });
      // Set side menu item as active using array index passed as prop
      this.props.items[this.props.itemActive].active = true;
    }
  }

  public componentDidMount() {
    const parentElement = this.sideMenuContainer && this.sideMenuContainer.parentElement as HTMLElement;
    parentElement && parentElement.addEventListener('click', () => {
      this.hide(); // Hide side menu when click outside
    });
  }

  // For simplicity show() and hide() operations are implemented in imperative way
  public show() {
    this.sideMenuContainer.style.display = 'block';
    this.sideMenuContainer.classList.remove('slide-out-left');
    this.sideMenuContainer.classList.add('slide-in-left');
  }

  public hide() {
    this.sideMenuContainer.classList.add('slide-out-left');
    this.sideMenuContainer.classList.remove('slide-in-left');
  }

  public render() {
    return (
      <div ref={ (sideMenuContainer: HTMLDivElement) => this.sideMenuContainer = sideMenuContainer }
        className="sideMenuContainer slide-in-left">
        <div className="sideMenuContent">
          <h1 className="side-menu-title">{ this.props.title }</h1>
          <hr/>
          <div className="side-menu-btn-close" onClick={ this.hide.bind(this) }>
            <img src={ btnCloseSvg } className="vertical-center"/>
          </div>
          <div className="items-container">
            {
              this.props.items && this.props.items.map( (item: ISideMenuItem) => {
                return (
                  <div key={ item.name } className={ item.active ? "side-menu-item active" : "side-menu-item" }>
                    <img src={ item.ico } className="icon-item" />
                    <a href={ item.url }>{ item.name }</a>
                  </div>
                )
              })
            }
            { this.props.children && <div className="side-menu-item">{ this.props.children }</div> }
          </div>
        </div>
      </div>
    )
  }
}

export interface ISideMenuItem {
  name: string;
  url?: string;
  ico?: string;
  active: boolean;
}

export const SIDE_MENU_ITEMS: ISideMenuItem[] = [
  {name: 'Home', url: '#/', ico: require('./icons/start.svg'), active: false},
  {name: '2D/3D Video', url: '#/2dvideo', ico: require('./icons/video-player.svg'), active: false},
  {name: '360 Video', url: '#/360video', ico: require('./icons/360-degrees.svg'), active: false},
  {name: '3D Model Animation', url: '#/3dmodel', ico: require('./icons/3d-printer.svg'), active: false},
];

import React from 'react';
import './TopMenuCmp.css';
const burgerIconSvg = require('./burger-icon.svg');
const leftArrowSvg = require('./left-arrow.svg');


export default class TopMenu extends React.PureComponent {

  public props: {
    leftIcon?: string;
    onClickMenuBtn?: Function;
    children?: any;
    [attrs: string]: any;
  };

  private onClickMenuBtn = () => {
    this.props.onClickMenuBtn && this.props.onClickMenuBtn();
  };

  private onClickBtnGoBack = () => {
    window.history.back();
  };

  public render(){
    //todo: refactor
    // Not root route => Render top left arrow button
    if (window.location.hash !== "#/") {
      return(
        <div className="top-menu">
          <img src={ leftArrowSvg } className="top-menu-icon-left" onClick={ this.onClickBtnGoBack }/>
          { this.props.children }
          <img src={ this.props.leftIcon || burgerIconSvg } className="top-menu-icon-right"
            onClick={ this.onClickMenuBtn }/>
        </div>
      )
      // In root route => not to render top left arrow button
    } else {
      return (
        <div className="top-menu">
          { this.props.children }
          <img src={ this.props.leftIcon || burgerIconSvg } className="top-menu-icon-right"
            onClick={ this.onClickMenuBtn }/>
        </div>
      )
    }
  }
}

